            currentSavings += yearlyInterest + yearlyContribution;
            totalInterest += yearlyInterest;
            yearlyContributionT +=yearlyContribution;
            yearlyData.push({
                year: Math.round(i + 1),
                savingsEndOfYear: Math.round(currentSavings),
                yearlyInterest: Math.round(yearlyInterest),
