    }

    return (
        <div>
            <header className="header">
                <img src={logo} alt="logo"/>
                <h1>Investment Calculator</h1>
            </header>
            <UserInputs onCalculateHandler={calculateHandler} onSaveData={saveTabData}></UserInputs>


            <UserOutput data={finalData}></UserOutput>
        </div>
    );
}

export default App;
