import logo from './assets/investment-calculator-logo.png';
import UserInputs from "./components/userImport/UserInputs";

import UserOutput from "./components/useroutputs/UserOutput";
import {useState} from "react";

function App() {
//arrivé des données:
    const [tabData, setTabData] = useState([]);

    const [finalData, setFinalData] = useState({
        year: "",
        totalSave: "",
        interest: "",
        totalInt: "",
        invested: ""
    })

    function saveTabData(entry) {
        setTabData((prevData) => [...prevData, entry]);
        console.log(tabData);
    }


    //sortie des données:

    const [test, setTest] = useState(0);


    const calculateHandler = (userInput) => {

        // Should be triggered when form is submitted
        // You might not directly want to bind it to the submit event on the form though...

        const yearlyData = []; // per-year results

        let currentSavings = +userInput['current-savings']; // feel free to change the shape of this input object!
        const yearlyContribution = +userInput['yearly-contribution']; // as mentioned: feel free to change the shape...
        const expectedReturn = +userInput['expected-return'] / 100;
        const duration = +userInput['duration'];

        // The below code calculates yearly results (total savings, interest etc)
        for (let i = 0; i < duration; i++) {
            const yearlyInterest = currentSavings * expectedReturn;
            currentSavings += yearlyInterest + yearlyContribution;
            yearlyData.push({
                // feel free to change the shape of the data pushed to the array!
                year: i + 1,
                yearlyInterest: yearlyInterest,
                savingsEndOfYear: currentSavings,
                yearlyContribution: yearlyContribution,

            });
        }
    };

    return (
        <div>
            <header className="header">
                <img src={logo} alt="logo"/>
                <h1>Investment Calculator</h1>
            </header>
            <UserInputs onSaveData={saveTabData}></UserInputs>


            <UserOutput data={tabData}></UserOutput>
        </div>
    );
}

export default App;
